/* eslint-disable @typescript-eslint/no-explicit-any */

import React, { useEffect, useState } from 'react';
import { useData } from '@/providers/DataProvider';

interface AuditReport {
    totalSessions: number;
    sessionsWithoutBlocks: any[];
    sessionsZeroDuration: any[];
    sessionsNoRating: any[];
    durationMismatches: any[];
    duplicates: any[];
}

export default function AuditPage() {
    const api = useData();
    const [report, setReport] = useState<AuditReport | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const runAudit = async () => {
            try {
                console.log('Starting audit...');
                const sessions = await api.registrosSesion.list();
                console.log('Fetched sessions:', sessions.length);

                const totalSessions = sessions.length;
                const sessionsWithoutBlocks = sessions.filter(s => !s.registrosBloque || s.registrosBloque.length === 0);
                const sessionsZeroDuration = sessions.filter(s => s.duracionRealSeg === 0);
                const sessionsNoRating = sessions.filter(s => s.calificacion === null || s.calificacion === undefined);

                const durationMismatches = sessions.filter(s => {
                    if (!s.registrosBloque) return false;
                    const blocksDuration = s.registrosBloque.reduce((acc: number, b: any) => acc + (b.duracionRealSeg || 0), 0);
                    // Allow small difference for floating point or minor inconsistencies (e.g. 1 second)
                    return Math.abs((s.duracionRealSeg || 0) - blocksDuration) > 1;
                }).map(s => ({
                    id: s.id,
                    sessionDuration: s.duracionRealSeg,
                    blocksDuration: (s.registrosBloque || []).reduce((acc: number, b: any) => acc + (b.duracionRealSeg || 0), 0),
                    diff: (s.duracionRealSeg || 0) - (s.registrosBloque || []).reduce((acc: number, b: any) => acc + (b.duracionRealSeg || 0), 0)
                }));

                // Check for duplicates (same start time within 1 minute)
                const sortedSessions = [...sessions].sort((a, b) => new Date(a.inicioISO).getTime() - new Date(b.inicioISO).getTime());
                const duplicates = [];
                for (let i = 0; i < sortedSessions.length - 1; i++) {
                    const current = sortedSessions[i];
                    const next = sortedSessions[i + 1];
                    const timeDiff = Math.abs(new Date(current.inicioISO).getTime() - new Date(next.inicioISO).getTime());
                    if (timeDiff < 60000) { // Less than 1 minute difference
                        duplicates.push({
                            id1: current.id,
                            id2: next.id,
                            time1: current.inicioISO,
                            time2: next.inicioISO,
                            diffMs: timeDiff
                        });
                    }
                }

                setReport({
                    totalSessions,
                    sessionsWithoutBlocks,
                    sessionsZeroDuration,
                    sessionsNoRating,
                    durationMismatches,
                    duplicates
                });
                (window as any)._auditReport = {
                    totalSessions,
                    sessionsWithoutBlocks,
                    sessionsZeroDuration,
                    sessionsNoRating,
                    durationMismatches,
                    duplicates
                };
                console.log('AUDIT_REPORT:', JSON.stringify({
                    totalSessions,
                    sessionsWithoutBlocks,
                    sessionsZeroDuration,
                    sessionsNoRating,
                    durationMismatches,
                    duplicates
                }));
            } catch (err: any) {
                console.error('Audit failed:', err);
                setError(err.message || 'Unknown error');
            } finally {
                setLoading(false);
            }
        };

        runAudit();
    }, []);

    if (loading) return <div className="p-8">Running audit... check console</div>;
    if (error) return <div className="p-8 text-red-500">Error: {error}</div>;
    if (!report) return <div className="p-8">No report generated</div>;

    return (
        <div className="p-8 max-w-4xl mx-auto space-y-8">
            <h1 className="text-2xl font-bold">Session Data Audit Report</h1>

            <section className="space-y-4">
                <h2 className="text-xl font-semibold">Summary</h2>
                <div className="grid grid-cols-2 gap-4">
                    <div className="p-4 bg-gray-100 rounded">
                        <div className="text-sm text-gray-500">Total Sessions</div>
                        <div className="text-2xl font-bold">{report.totalSessions}</div>
                    </div>
                    <div className={`p-4 rounded ${report.sessionsWithoutBlocks.length > 0 ? 'bg-red-100' : 'bg-green-100'}`}>
                        <div className="text-sm text-gray-500">Sessions w/o Blocks</div>
                        <div className="text-2xl font-bold">{report.sessionsWithoutBlocks.length}</div>
                    </div>
                    <div className={`p-4 rounded ${report.sessionsZeroDuration.length > 0 ? 'bg-yellow-100' : 'bg-green-100'}`}>
                        <div className="text-sm text-gray-500">Zero Duration</div>
                        <div className="text-2xl font-bold">{report.sessionsZeroDuration.length}</div>
                    </div>
                    <div className={`p-4 rounded ${report.sessionsNoRating.length > 0 ? 'bg-orange-100' : 'bg-green-100'}`}>
                        <div className="text-sm text-gray-500">Missing Rating</div>
                        <div className="text-2xl font-bold">{report.sessionsNoRating.length}</div>
                    </div>
                    <div className={`p-4 rounded ${report.durationMismatches.length > 0 ? 'bg-red-100' : 'bg-green-100'}`}>
                        <div className="text-sm text-gray-500">Duration Mismatches</div>
                        <div className="text-2xl font-bold">{report.durationMismatches.length}</div>
                    </div>
                    <div className={`p-4 rounded ${report.duplicates.length > 0 ? 'bg-yellow-100' : 'bg-green-100'}`}>
                        <div className="text-sm text-gray-500">Potential Duplicates</div>
                        <div className="text-2xl font-bold">{report.duplicates.length}</div>
                    </div>
                </div>
            </section>

            {/* Detailed sections commented out for stability */}
            {/*
            {report.sessionsWithoutBlocks.length > 0 && (
                <section>
                    <h2 className="text-xl font-semibold mb-2">Sessions Without Blocks</h2>
                    <div className="bg-gray-50 p-4 rounded overflow-auto max-h-60">
                        <pre className="text-xs">{JSON.stringify(report.sessionsWithoutBlocks.map(s => ({ id: s.id, date: s.inicioISO })), null, 2)}</pre>
                    </div>
                </section>
            )}
            {report.sessionsZeroDuration.length > 0 && (
                <section>
                    <h2 className="text-xl font-semibold mb-2">Sessions with Zero Duration</h2>
                    <div className="bg-gray-50 p-4 rounded overflow-auto max-h-60">
                        <pre className="text-xs">{JSON.stringify(report.sessionsZeroDuration.map(s => ({ id: s.id, date: s.inicioISO })), null, 2)}</pre>
                    </div>
                </section>
            )}
            {report.sessionsNoRating.length > 0 && (
                <section>
                    <h2 className="text-xl font-semibold mb-2">Sessions with No Rating</h2>
                    <div className="bg-gray-50 p-4 rounded overflow-auto max-h-60">
                        <pre className="text-xs">{JSON.stringify(report.sessionsNoRating.map(s => ({ id: s.id, date: s.inicioISO })), null, 2)}</pre>
                    </div>
                </section>
            )}
            {report.durationMismatches.length > 0 && (
                <section>
                    <h2 className="text-xl font-semibold mb-2">Duration Mismatches</h2>
                    <div className="bg-gray-50 p-4 rounded overflow-auto max-h-60">
                        <pre className="text-xs">{JSON.stringify(report.durationMismatches, null, 2)}</pre>
                    </div>
                </section>
            )}
            {report.duplicates.length > 0 && (
                <section>
                    <h2 className="text-xl font-semibold mb-2">Potential Duplicate Sessions</h2>
                    <div className="bg-gray-50 p-4 rounded overflow-auto max-h-60">
                        <pre className="text-xs">{JSON.stringify(report.duplicates, null, 2)}</pre>
                    </div>
                </section>
            )}
            */}

            {report && (
                <div className="mt-8 border-t pt-8">
                    <h2 className="text-xl font-bold mb-4">Raw Report Data (for extraction)</h2>
                    <pre id="audit-report-text" className="bg-slate-100 p-4 rounded text-xs overflow-auto max-h-96 select-all">
                        {JSON.stringify(report, null, 2)}
                    </pre>
                </div>
            )}
        </div>
    );
}

/* eslint-disable @typescript-eslint/no-explicit-any */
/* eslint-disable @typescript-eslint/no-unused-vars */
import React, { useState, useEffect, useMemo } from "react";
import { localDataClient } from "@/api/localDataClient";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useBloques } from "@/features/estudio/hooks/useBloques";
import { useAsignaciones } from "@/features/asignaciones/hooks/useAsignaciones";
import { useUsers } from "@/features/shared/hooks/useUsers";
// Updated Card, Badge, Alert paths from @/components/ui to @/components/ds
import { Card, CardContent, CardHeader, CardTitle } from "@/features/shared/components/ds";
import { Button } from "@/features/shared/components/ui/button";
import { Input } from "@/features/shared/components/ui/input";
import { Label } from "@/features/shared/components/ui/label";
import { Textarea } from "@/features/shared/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/features/shared/components/ui/select";
import { Badge } from "@/features/shared/components/ds";
import { Alert, AlertDescription } from "@/features/shared/components/ds";
import {
  ArrowLeft, Target, User, Music, BookOpen, Calendar, Settings, ChevronDown, ChevronRight, Clock, Edit, XCircle, Shield, Save, X, SlidersHorizontal, Trash2, // Existing icons
  Eye, CheckCircle2, Layers, PlayCircle, MessageSquare // New icons from outline
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { createPageUrl } from "@/utils";
import { toast } from "sonner";
// Preserving all helper functions and components used in the original file
import { getNombreVisible, formatLocalDate, parseLocalDate, startOfMonday, resolveUserIdActual } from "@/features/shared/utils/helpers";
import { useEffectiveUser } from "@/providers/EffectiveUserProvider";
import { calcularTiempoSesion } from "@/features/estudio/components/sessionSequence";
import SessionContentView from "@/features/shared/components/study/SessionContentView";
import { PageHeader } from "@/features/shared/components/ds/PageHeader";
import { LoadingSpinner } from "@/features/shared/components/ds";
import { componentStyles } from "@/design/componentStyles";

export default function AsignacionDetallePage() {
  const queryClient = useQueryClient();
  const navigate = useNavigate();
  const [expandedSemanas, setExpandedSemanas] = useState<Set<number>>(new Set([0]));
  const [expandedSesiones, setExpandedSesiones] = useState<Set<string>>(new Set());
  const [showEditDrawer, setShowEditDrawer] = useState(false);
  const [editData, setEditData] = useState<any>(null);

  const urlParams = new URLSearchParams(window.location.search);
  const asignacionId = urlParams.get('id') || '';

  const effectiveUser = useEffectiveUser();


  // Query para obtener bloques con hook centralizado
  const { data: dbBloques = [], isLoading: isLoadingBloques, isError: isErrorBloques } = useBloques();

  const { data: allAsignaciones = [], isLoading: isLoadingAsignaciones } = useAsignaciones();

  const asignacion = useMemo(() =>
    allAsignaciones.find(a => a.id === asignacionId),
    [allAsignaciones, asignacionId]
  );

  const isLoading = isLoadingAsignaciones;

  // Query para obtener usuarios con hook centralizado
  const { data: usuarios = [] } = useUsers();

  // Resolver ID de usuario actual de la BD
  const userIdActual = useMemo(() => {
    return resolveUserIdActual(effectiveUser, usuarios);
  }, [effectiveUser, usuarios]);

  const { data: piezas = [] } = useQuery({
    queryKey: ['piezas'],
    queryFn: () => localDataClient.entities.Pieza.list(),
  });

  const cerrarMutation = useMutation({
    mutationFn: () => localDataClient.entities.Asignacion.update(asignacionId, { estado: 'cerrada' }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['asignaciones'] });
      toast.success(' Asignacin cerrada');
    },
  });

  const eliminarMutation = useMutation({
    mutationFn: () => localDataClient.entities.Asignacion.delete(asignacionId),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['asignaciones'] });
      toast.success(' Asignacin eliminada');
      navigate(-1);
    },
    onError: (error) => {
      console.error('[asignacion-detalle.jsx] Error al eliminar asignacin:', error);
      toast.error(' Error al eliminar asignacin');
    },
  });

  const publicarMutation = useMutation({
    mutationFn: () => localDataClient.entities.Asignacion.update(asignacionId, { estado: 'publicada' }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['asignaciones'] });
      toast.success(' Asignacin publicada');
    },
  });

  const editarMutation = useMutation({
    mutationFn: async (data: any) => {
      if (data.piezaId && asignacion && data.piezaId !== asignacion.piezaId) {
        const pieza = piezas.find(p => p.id === data.piezaId);
        if (pieza) {
          data.piezaSnapshot = {
            nombre: pieza.nombre,
            descripcion: pieza.descripcion || '',
            nivel: pieza.nivel,
            tiempoObjetivoSeg: pieza.tiempoObjetivoSeg || 0,
            elementos: pieza.elementos || [],
          };
        }
      }

      return localDataClient.entities.Asignacion.update(asignacionId, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['asignaciones'] });
      toast.success(' Cambios guardados');
      setShowEditDrawer(false);
    },
    onError: (error: any) => {
      console.error('[asignacion-detalle.jsx] Error al actualizar asignacin:', error);
      let errorMsg = ' Error al guardar cambios. Intntalo de nuevo.';

      // Mensajes especficos segn el tipo de error
      if (error?.code === 'PGRST204' || error?.code === '406') {
        errorMsg = ' Error 406: El servidor rechaz la actualizacin. El campo "plan" no puede actualizarse directamente. Solo se pueden actualizar: notas, foco, estado, semanaInicioISO y piezaId.';
      } else if (error?.code === 'PGRST301' || error?.code === '23503') {
        errorMsg = ' Error de integridad: Verifica que la pieza (piezaId) exista en la base de datos.';
      } else if (error?.code === '42501' || error?.status === 403) {
        errorMsg = ' Error de permisos: No tienes permisos para actualizar esta asignacin. Verifica tu rol de usuario.';
      } else if (error?.message) {
        errorMsg = ` Error: ${error.message}`;
      }

      toast.error(errorMsg);
    },
  });

  const calcularLunesSemanaISO = (fecha: string | Date) => {
    if (!fecha) return formatLocalDate(startOfMonday(new Date()));
    try {
      let date: Date;
      if (typeof fecha === 'string') {
        date = parseLocalDate(fecha);
      } else {
        date = fecha;
      }
      return formatLocalDate(startOfMonday(date));
    } catch (error) {
      return formatLocalDate(startOfMonday(new Date()));
    }
  };

  // Actualizar semanaInicioISO cuando cambia fechaSeleccionada
  useEffect(() => {
    if (editData?.fechaSeleccionada) {
      const lunes = calcularLunesSemanaISO(editData.fechaSeleccionada);
      setEditData((prev: any) => ({ ...prev, semanaInicioISO: lunes }));
    }
  }, [editData?.fechaSeleccionada]);

  // Verificar si el usuario puede editar el profesor asignado
  const puedeEditarProfesor = useMemo(() => {
    if (!asignacion || !effectiveUser) return false;
    const isAdmin = (effectiveUser as any).rolPersonalizado === 'ADMIN';
    if (isAdmin) return true;

    // Si es profesor, solo puede editar si es su asignacin
    const isProf = (effectiveUser as any).rolPersonalizado === 'PROF';
    if (isProf) {
      // Verificar si el profesorId de la asignacin coincide con el ID del usuario actual
      // (considerando posibles desincronizaciones entre auth y BD)
      return asignacion.profesorId === userIdActual || asignacion.profesorId === (effectiveUser as any)?.id;
    }

    return false;
  }, [asignacion, effectiveUser, userIdActual]);

  // Obtener lista de profesores disponibles
  const profesoresDisponibles = useMemo(() => {
    return usuarios
      .filter(u => u.rolPersonalizado === 'PROF')
      .map(p => ({
        value: p.id,
        label: getNombreVisible(p),
      }))
      .sort((a, b) => a.label.localeCompare(b.label));
  }, [usuarios]);

  const handleEditarInformacion = () => {
    if (!asignacion) return;
    // Usar semanaInicioISO si existe, sino usar la fecha actual
    const fechaInicial = asignacion.semanaInicioISO
      ? asignacion.semanaInicioISO
      : formatLocalDate(new Date());

    console.log('Inicializando edicin con fecha:', fechaInicial, 'de asignacin:', asignacion.semanaInicioISO);

    setEditData({
      piezaId: asignacion.piezaId,
      fechaSeleccionada: fechaInicial,
      semanaInicioISO: fechaInicial,
      foco: asignacion.foco || 'GEN',
      notas: asignacion.notas || '',
      profesorId: asignacion.profesorId || null,
    });
    setShowEditDrawer(true);
  };

  // Atajos de teclado para el modal de edicin
  useEffect(() => {
    if (!showEditDrawer) return;

    const handleKeyDown = (e: KeyboardEvent) => {
      if ((e.ctrlKey || e.metaKey) && e.key === '.') {
        e.preventDefault();
        setShowEditDrawer(false);
      }
      if ((e.ctrlKey || e.metaKey) && e.key === 'Enter') {
        e.preventDefault();
        handleGuardarEdicion();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [showEditDrawer, editData]);

  const handleGuardarEdicion = () => {
    console.log('[handleGuardarEdicion] Called with editData:', editData);
    if (!editData) {
      toast.error(' Error: No hay datos para guardar');
      return;
    }
    if (!asignacion) return;
    console.log('[handleGuardarEdicion] piezaId:', editData.piezaId, 'fechaSeleccionada:', editData.fechaSeleccionada);
    // piezaId can be null (optional field now)
    if (!editData.fechaSeleccionada || editData.fechaSeleccionada.trim() === '') {
      toast.error(' Debes seleccionar una fecha');
      return;
    }
    console.log('[handleGuardarEdicion] Passed fecha validation');

    // Only show confirmation when changing from one pieza to another (not when removing pieza)
    if (editData.piezaId && editData.piezaId !== asignacion.piezaId) {
      if (!window.confirm('Cambiar pieza actualizar el material de FM. Continuar?')) {
        return;
      }
    }
    console.log('[handleGuardarEdicion] Passed pieza confirm check');

    const semanaInicioISO = calcularLunesSemanaISO(editData.fechaSeleccionada);
    console.log('[handleGuardarEdicion] semanaInicioISO:', semanaInicioISO);
    if (!semanaInicioISO) {
      toast.error(' Error al calcular la fecha de inicio');
      return;
    }
    console.log('[handleGuardarEdicion] Passed semana validation');

    // Validar foco: solo valores permitidos
    const focoPermitidos = ['GEN', 'LIG', 'RIT', 'ART', 'S&A'];
    const foco = editData.foco || 'GEN';
    console.log('[handleGuardarEdicion] foco:', foco, 'permitidos:', focoPermitidos);
    if (!focoPermitidos.includes(foco)) {
      toast.error(` Valor de foco no vlido. Debe ser uno de: ${focoPermitidos.join(', ')}`);
      return;
    }
    console.log('[handleGuardarEdicion] Passed foco validation');

    // Validar notas: string o null (no puede ser undefined)
    const notas = editData.notas && editData.notas.trim() !== ''
      ? editData.notas.trim()
      : null;

    const dataToSave: any = {
      piezaId: editData.piezaId,
      semanaInicioISO: semanaInicioISO,
      foco: foco,
      notas: notas, // null si est vaco
    };
    console.log('[handleGuardarEdicion] dataToSave created:', dataToSave);

    // Incluir profesorId solo si el usuario tiene permisos para cambiarlo
    console.log('[handleGuardarEdicion] puedeEditarProfesor:', puedeEditarProfesor, 'editData.profesorId:', editData.profesorId, 'original:', asignacion?.profesorId);
    if (puedeEditarProfesor && editData.profesorId !== undefined) {
      // Solo validar si el profesor cambi respecto al original
      if (editData.profesorId !== asignacion?.profesorId && editData.profesorId !== null) {
        // Validar que el nuevo profesorId existe en la lista de profesores
        const profesorExiste = profesoresDisponibles.some(p => p.value === editData.profesorId);
        console.log('[handleGuardarEdicion] profesorExiste:', profesorExiste, 'profesoresDisponibles:', profesoresDisponibles.length);
        if (!profesorExiste) {
          toast.error(' El profesor seleccionado no es vlido');
          return;
        }
      }
      dataToSave.profesorId = editData.profesorId || null;
    }
    console.log('[handleGuardarEdicion] Final dataToSave:', dataToSave);

    // Campos permitidos en update: notas, foco, estado, semanaInicioISO, piezaId, piezaSnapshot (solo si piezaId cambi), profesorId (solo si tiene permisos)
    // Campos PROHIBIDOS en update: plan (solo se actualiza en create completo)

    console.log('[handleGuardarEdicion] Calling editarMutation.mutate...');
    editarMutation.mutate(dataToSave);
  };

  const toggleSemana = (index: number) => {
    const newExpanded = new Set(expandedSemanas);
    if (newExpanded.has(index)) {
      newExpanded.delete(index);
    } else {
      newExpanded.add(index);
    }
    setExpandedSemanas(newExpanded);
  };

  const toggleSesion = (semanaIndex: number, sesionIndex: number) => {
    const key = `${semanaIndex}-${sesionIndex}`;
    const newExpanded = new Set(expandedSesiones);
    if (newExpanded.has(key)) {
      newExpanded.delete(key);
    } else {
      newExpanded.add(key);
    }
    setExpandedSesiones(newExpanded);
  };

  const focoLabels = {
    GEN: 'General',
    SON: 'Sonido',
    FLX: 'Flexibilidad',
    MOT: 'Motricidad',
    ART: 'Articulacin',
    COG: 'Cognitivo',
  };

  const focoColors = {
    GEN: componentStyles.status.badgeDefault,
    LIG: componentStyles.status.badgeInfo,
    RIT: componentStyles.status.badgeDefault, // Usar default para prpura
    ART: componentStyles.status.badgeSuccess,
    'S&A': componentStyles.status.badgeDefault, // Usar default para brand
  };

  const estadoColors = {
    borrador: componentStyles.status.badgeDefault,
    publicada: componentStyles.status.badgeSuccess,
    en_curso: componentStyles.status.badgeInfo,
    cerrada: componentStyles.status.badgeWarning,
  };

  const estadoLabels = {
    borrador: 'Borrador',
    publicada: 'Publicada',
    en_curso: 'En Curso',
    cerrada: 'Cerrada',
  };

  if (isLoading) {
    return (
      <LoadingSpinner
        size="xl"
        variant="fullPage"
        text="Cargando asignacin..."
      />
    );
  }

  if (!asignacion) {
    return (
      <div className="flex items-center justify-center min-h-[80vh]">
        <Card className={`max-w-md ${componentStyles.containers.cardBase}`}>
          <CardContent className="pt-6 text-center space-y-4">
            <Shield className={`w-16 h-16 mx-auto ${componentStyles.empty.emptyIcon} text-[var(--color-danger)]`} />
            <div>
              <h2 className="text-lg md:text-xl font-semibold text-[var(--color-text-primary)] mb-2 font-headings">Asignacin No Encontrada</h2>
              <p className="text-sm md:text-base text-[var(--color-text-primary)]">No tienes acceso a esta asignacin o no existe.</p>
              <Button onClick={() => navigate(-1)} className={`mt-4 ${componentStyles.buttons.primary}`}>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Volver
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!asignacion) {
    return null; // Early return si asignacion es undefined
  }

  const alumno = usuarios.find(u => u.id === asignacion.alumnoId);
