          userRole={userRole as any}
          onDelete={handleDeleteAsignacion}
        />
      )}
      {tipoEventoSeleccionado === 'evento' && (
        isEstu && eventoSeleccionado ? (
          <ModalEventoResumen
            open={true}
            onOpenChange={handleCerrarModal}
            evento={eventoSeleccionado}
          />
        ) : (
          <ModalCrearEvento
            open={true}
            onOpenChange={handleCerrarModal}
            evento={eventoSeleccionado}
            userIdActual={userIdActual || ""}
          />
