} from "react";

import {
  DEFAULT_DESIGN,
  generateCSSVariables,
  normalizeDesign,
  DesignTokens,
  DesignBase,
  DesignOverlay,
  DesignChangePartitioned,
  ModeValue,
  // New imports for refactored model
  createDefaultDesignBase,
  migrateToDesignBase,
  mergeDesignWithMode,
  diffDesignPartitioned,
  applyChangeToOverlay,
  revertChangeInOverlay,
} from "./designConfig";
import {
  getAllPresets,
  deleteCustomPreset,
  isBuiltInPreset,
} from "./DesignPresets";
