import { SortableItem } from "@/features/shared/components/dnd/SortableItem";
// import { calculateSessionTime } from "@/utils/variationUtils";
// import { formatDurationMinutes } from "@/features/shared/utils/helpers";
import { Clock } from "lucide-react";
import { Ejercicio, SessionFormData, Ronda, SecuenciaItem, Pieza, Variation } from "@/features/editor/types";

interface SortableRondaProps {
  id: string;
  ronda: Ronda;
  seqIndex: number;
  isExpanded: boolean;
  formData: SessionFormData;
  expandedRondas: Set<number>;
  setExpandedRondas: React.Dispatch<React.SetStateAction<Set<number>>>;
  updateRondaRepeticiones: (id: string, value: string) => void;
  updateRondaAleatoria: (id: string, value: boolean) => void;
  duplicateRonda: (id: string) => void;
  setFormData: React.Dispatch<React.SetStateAction<SessionFormData>>;
  setEditingEjercicio: (value: { index?: number; ejercicio: Ejercicio | null; piezaSnapshot: Pieza | null } | null) => void;
  removeEjercicioFromRonda: (rondaIndex: number, code: string) => void;
  toggleEjercicioModo: (index: number) => void;
  piezaSnapshot: Pieza | null;
  pieza: Pieza;
  tipoColors: Record<string, string>;
  componentStyles: any;
}

function SortableRonda({
  id,
  ronda,
  seqIndex,
  isExpanded,
  formData,
  expandedRondas: _expandedRondas,
  setExpandedRondas,
  updateRondaRepeticiones,
  updateRondaAleatoria,
  duplicateRonda,
  setFormData,
  setEditingEjercicio,
  removeEjercicioFromRonda,
  toggleEjercicioModo,
  piezaSnapshot,
  pieza,
  tipoColors,
  componentStyles
}: SortableRondaProps) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <Card
      ref={setNodeRef}
      style={style}
      className={`app-panel border-[var(--color-primary)]/30 bg-[var(--color-primary-soft)] ${isDragging ? 'shadow-card border-[var(--color-primary)]' : ''
        }`}
    >
      <CardContent className="pt-4">
        <div
          className="flex items-center gap-2 mb-2 cursor-pointer hover:bg-[var(--color-primary-soft)] rounded-[var(--radius-card)] p-1 -m-1 transition-colors"
          onClick={() => setExpandedRondas(prev => {
            const n = new Set(prev);
            if (n.has(seqIndex)) {
              n.delete(seqIndex);
            } else {
              n.add(seqIndex);
            }
            return n;
          })}
        >
          <div {...attributes} {...listeners} className="cursor-grab active:cursor-grabbing" onClick={(e) => e.stopPropagation()}>
            <GripVertical className="w-4 h-4 text-[var(--color-primary)]" />
          </div>
          <div className="pt-0.5">
            {isExpanded ? (
              <ChevronDown className="w-4 h-4 text-[var(--color-primary)]" />
            ) : (
              <ChevronRight className="w-4 h-4 text-[var(--color-primary)]" />
            )}
          </div>
          <Badge className="bg-[var(--color-primary)] text-[var(--color-text-inverse)] rounded-full">Ronda</Badge>
          <div className="flex items-center gap-2" onClick={(e) => e.stopPropagation()}>
            <span className="text-sm font-medium"></span>
            <Input
              type="number"
              min="1"
              value={ronda.repeticiones}
              onChange={(e) => updateRondaRepeticiones(ronda.id, e.target.value)}
              className={`w-16 h-8 text-center ${componentStyles.controls.inputDefault}`}
            />
            <span className="text-sm text-[var(--color-text-secondary)]">repeticiones</span>
          </div>
          <span className="text-sm text-[var(--color-text-secondary)] ml-auto">({ronda.bloques.length} ejercicios)</span>
          <div className="flex items-center gap-1" onClick={(e) => e.stopPropagation()}>
            <Label className="text-xs text-[var(--color-text-secondary)] cursor-pointer flex items-center gap-1">
              <Checkbox
                checked={!!ronda.aleatoria}
                onCheckedChange={(checked) => updateRondaAleatoria(ronda.id, !!checked)}
              />
              <Shuffle className="w-3 h-3" />
              aleatorio
            </Label>
          </div>
          <div className="flex gap-1" onClick={(e) => e.stopPropagation()}>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => duplicateRonda(ronda.id)}
              title="Duplicar ronda"
              className={componentStyles.buttons.editIcon}
            >
              <Copy className="w-4 h-4" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => {
                if (window.confirm('Eliminar esta ronda?')) {
                  setFormData(prev => {
                    const newRondas = prev.rondas.filter(r => r.id !== ronda.id);
                    const newSeq = prev.secuencia.filter(s => !(s.kind === 'RONDA' && s.id === ronda.id));
                    return { ...prev, rondas: newRondas, secuencia: newSeq };
                  });
                }
              }}
              className={componentStyles.buttons.deleteIcon}
            >
              <Trash2 className="w-4 h-4" />
            </Button>
          </div>
        </div>

        {isExpanded && (
          <SortableContext
            items={ronda.bloques.map((code, i) => `${ronda.id}-r-${code}-${i}`)}
            strategy={verticalListSortingStrategy}
          >
            <div className="ml-6 mt-3 space-y-2 border-l-2 border-[var(--color-primary)]/30 pl-4">
              {ronda.bloques.map((code, eIndex) => {
                const ejercicio = formData.bloques.find(b => b.code === code);
                if (!ejercicio) {
                  return (
                    <div key={`${ronda.id}-${eIndex}`} className="p-2 bg-[var(--color-danger)]/10 border border-[var(--color-danger)]/20 rounded-[var(--radius-card)] text-sm text-[var(--color-danger)] flex items-center gap-2">
                      <AlertTriangle className="w-4 h-4" />
                       Referencia hurfana: {code}
                    </div>
                  );
                }

                return (
                  <SortableItem
                    key={`${ronda.id}-${code}-${eIndex}`}
                    id={`${ronda.id}-r-${code}-${eIndex}`}
                    className="flex items-center gap-2 p-2 bg-[var(--color-surface-elevated)] border border-[var(--color-border-default)] rounded-[var(--radius-card)] cursor-pointer hover:border-[var(--color-primary)] transition-colors"
                    onClick={() => {
                      const idx = formData.bloques.findIndex(b => b.code === code);
                      if (idx !== -1) {
                        setEditingEjercicio({
                          index: idx,
                          ejercicio,
                          piezaSnapshot: piezaSnapshot || pieza
                        });
                      }
                    }}
                  >
                    {({ dragHandleProps, isDragging: _isDragging }) => (
                      <>
                        <div {...dragHandleProps} className="cursor-grab active:cursor-grabbing p-1 -m-1" onClick={(e) => e.stopPropagation()}>
                          <GripVertical className="w-3 h-3 text-[var(--color-text-secondary)]" />
                        </div>
                        <Badge variant="outline" className={`shrink-0 rounded-full ${tipoColors[ejercicio.tipo as keyof typeof tipoColors] || tipoColors.TC}`}>
                          {ejercicio.tipo}
                        </Badge>
                        <span className="text-sm flex-1 text-[var(--color-text-primary)]">
                          {ejercicio.nombre} <span className="text-[var(--color-text-secondary)]">({ejercicio.code})</span>
                        </span>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            const idx = formData.bloques.findIndex(b => b.code === code);
                            if (idx !== -1) {
                              toggleEjercicioModo(idx);
                            }
                          }}
                          className={`${componentStyles.buttons.iconSmall} ${componentStyles.buttons.ghost} ${(ejercicio.modo || 'foco') === 'foco' ? 'text-amber-500 hover:text-amber-600 hover:bg-amber-50' : 'text-purple-500 hover:text-purple-600 hover:bg-purple-50'}`}
                          title={(ejercicio.modo || 'foco') === 'foco' ? 'Modo Foco (click para cambiar a Repaso)' : 'Modo Repaso (click para cambiar a Foco)'}
                        >
                          {(ejercicio.modo || 'foco') === 'foco' ? <Zap className="w-3 h-3" /> : <Eye className="w-3 h-3" />}
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            const idx = formData.bloques.findIndex(b => b.code === code);
                            if (idx !== -1) {
                              setEditingEjercicio({
                                index: idx,
                                ejercicio,
                                piezaSnapshot: piezaSnapshot || pieza
                              });
                            }
                          }}
                          className={`${componentStyles.buttons.iconSmall} ${componentStyles.buttons.ghost} ${componentStyles.buttons.editSubtle}`}
                          title="Editar ejercicio"
                        >
                          <Edit className="w-3 h-3" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            removeEjercicioFromRonda(
                              formData.rondas.findIndex(r => r.id === ronda.id),
                              code
                            );
                          }}
                          className={`${componentStyles.buttons.iconSmall} ${componentStyles.buttons.ghost} ${componentStyles.buttons.deleteSubtle}`}
                          title="Quitar de la ronda"
                        >
                          <X className="w-3 h-3" />
                        </Button>
                      </>
                    )}
                  </SortableItem>
                );
              })}
            </div>
          </SortableContext>
        )}
      </CardContent>
    </Card>
  );
}

interface SessionEditorProps {
  sesion: SessionFormData;
  pieza: Pieza;
  piezaSnapshot: Pieza | null;
  alumnoId: string;
  onSave: (data: SessionFormData) => void;
  onClose: () => void;
}

export default function SessionEditor({ sesion, pieza, piezaSnapshot, alumnoId, onSave, onClose }: SessionEditorProps) {
  const [formData, setFormData] = useState<SessionFormData>({
    nombre: '',
    foco: 'GEN',
    bloques: [],
    rondas: [],
    secuencia: [],
  });

  const [expandedRondas, setExpandedRondas] = useState<Set<number>>(new Set());
  const [editingEjercicio, setEditingEjercicio] = useState<{ index?: number; ejercicio: Ejercicio | null; piezaSnapshot: Pieza | null } | null>(null);
  const [selectedEjercicios, setSelectedEjercicios] = useState<Set<string>>(new Set());
  const [searchTerm, setSearchTerm] = useState('');
  const [tiposFilter, setTiposFilter] = useState<Set<string>>(new Set(['TC']));
  // const [isInlineMode, setIsInlineMode] = useState(false);

  const { data: ejercicios = [], isLoading: isLoadingEjercicios } = useQuery<Ejercicio[]>({
    queryKey: ['bloques-session-editor'], // Unique key to avoid collision with EjerciciosTab which returns raw snake_case
    queryFn: async () => {
      console.log('[SessionEditor] Fetching blocks from Supabase (forced unique key)...');
      const { data, error } = await supabase
        .from('bloques')
        .select('*')
        .order('created_at', { ascending: false })
        .limit(100);

      if (error) {
        console.error('[SessionEditor] Supabase error:', error);
        throw error;
      }

      console.log('[SessionEditor] RAW DATA count:', data?.length);
      if (data && data.length > 0) {
        console.log('[SessionEditor] Sample raw item:', data[0]);
      }

      const mappedData = (data || []).map(b => {
        // Map snake_case to camelCase manually for critical fields
        // This mirrors the logic in EjerciciosTab
        let vars = [];
        if (b.content && Array.isArray(b.content) && b.content.length > 0) {
          vars = b.content;
        }

        return {
          id: b.id,
          nombre: b.nombre || b.title || 'Sin nombre', // Fallback
          code: b.code,
          tipo: b.tipo,
          duracionSeg: b.duracion_seg || b.duracionSeg || 0,
          instrucciones: b.instrucciones,
          indicadorLogro: b.indicador_logro || b.indicadorLogro,
          materialesRequeridos: b.materiales_requeridos || b.materialesRequeridos || [],
          media: b.media_links || b.media || {},
          elementosOrdenados: b.elementos_ordenados || b.elementosOrdenados || [],
          // Map content/variations
          variations: vars,
          content: vars,
          // Keep raw just in case
          raw: b
        };
      });

      console.log('[SessionEditor] Fetched & Mapped:', mappedData.length);
      return mappedData;
    },
    staleTime: 5 * 60 * 1000, // 5 min cache - catalog data
  });

  // OPTIMIZACIN: Usar useUsers() para obtener datos cacheados
  const { data: allUsers = [] } = useUsers();

  const alumno = useMemo(() => {
    if (!alumnoId || !allUsers.length) return null;
    return allUsers.find(u => u.id === alumnoId) || null;
  }, [alumnoId, allUsers]);



  useEffect(() => {
    if (sesion) {
      // Asegurar ID estable en rondas
      const sesionConIds = ensureRondaIds(sesion);
      const rondasConId = sesionConIds.rondas || [];

      // Si no hay secuencia guardada, la creamos
      const secuenciaDefault = buildDefaultSecuencia(sesionConIds);
      const secuenciaFinal = sesion.secuencia || secuenciaDefault;

      setFormData({
        nombre: sesion.nombre || '',
        foco: sesion.foco || 'GEN',
        bloques: sesion.bloques || [],
        rondas: rondasConId,
        secuencia: secuenciaFinal,
      });

      // Expandir todas las rondas por defecto (basndose en su posicin en la secuencia)
      if (rondasConId.length > 0) {
        const allRondasExpanded = new Set(
          secuenciaFinal
            .map((item, seqIndex) => item.kind === 'RONDA' ? seqIndex : null)
            .filter(idx => idx !== null)
        );
        setExpandedRondas(allRondasExpanded);
      }
    }
  }, [sesion]);

  const handleRepararReferencias = useCallback(() => {
    const codesInSession = formData.bloques.map(b => b.code);
    const rondasReparadas = formData.rondas.map(r => ({
      ...r,
      bloques: r.bloques.filter(code => codesInSession.includes(code))
    })).filter(r => r.bloques.length > 0);

    setFormData({ ...formData, rondas: rondasReparadas });
    toast.success(' Referencias reparadas');
  }, [formData]);

  const handleSave = useCallback(() => {
    const codesInSession = formData.bloques.map(b => b.code);
    const rondasConHuerfanas = formData.rondas.some(r =>
      r.bloques.some(code => !codesInSession.includes(code))
    );

    if (rondasConHuerfanas) {
      if (window.confirm(' Esta sesin contiene referencias invlidas en las rondas. Deseas repararlas automticamente?')) {
        handleRepararReferencias();
        return;
      }
      return;
    }

    onSave(formData);
  }, [formData, onSave, handleRepararReferencias]);

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (editingEjercicio) return;

      if ((e.ctrlKey || e.metaKey) && e.key === '.') {
        e.preventDefault();
        onClose();
      }
      if ((e.ctrlKey || e.metaKey) && e.key === 'Enter') {
        e.preventDefault();
        handleSave();
      }
      if ((e.ctrlKey || e.metaKey) && e.key === 'b') {
        e.preventDefault();
        setSelectedEjercicios(new Set());
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [editingEjercicio, onClose, handleSave]);



  const filteredEjercicios = ejercicios.filter(e => {
    const name = e.nombre || e.title || '';
    const code = e.code || '';

    const matchSearch = name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      code.toLowerCase().includes(searchTerm.toLowerCase());
    const matchTipo = tiposFilter.size === 0 || tiposFilter.has(e.tipo || '');

    return matchSearch && matchTipo;
  });

  const toggleTipoFilter = (tipo: string) => {
    const newFilters = new Set(tiposFilter);
    if (newFilters.has(tipo)) {
      newFilters.delete(tipo);
    } else {
      newFilters.add(tipo);
    }
    setTiposFilter(newFilters);
  };

  useEffect(() => {
    console.log('[SessionEditor] Current filteredEjercicios:', filteredEjercicios.length);
    console.log('[SessionEditor] Total ejercicios:', ejercicios.length);
    if (ejercicios.length > 0) {
      console.log('[SessionEditor] Sample Data:', ejercicios[0]);
    }
  }, [filteredEjercicios.length, ejercicios.length]);

  const addEjerciciosSeleccionados = () => {
    const newBloquesToAdd: Ejercicio[] = [];
    const newSecItems: SecuenciaItem[] = [];

    selectedEjercicios.forEach(ejercicioId => {
      const ejercicio = ejercicios.find(e => e.id === ejercicioId);
      if (ejercicio) {
        const fullCode = ejercicio.code || '';
        const exists = formData.bloques.some(b => b.code === fullCode);
        if (exists && !window.confirm(`El ejercicio ${fullCode} ya existe. Aadir de nuevo?`)) {
          return;
        }

        const nuevoEjercicio: Ejercicio = {
          ...ejercicio,
          id: uid(), // New ID for the instance in session
          code: fullCode,
          nombre: ejercicio.nombre || ejercicio.title || '',
          // Ensure mandatory fields for local state
          media: ejercicio.media || {},
          elementosOrdenados: ejercicio.elementosOrdenados || [],
          // Include variations in snapshot
          variations: (ejercicio.variations || ejercicio.content || []) as any as Variation[],
          content: (ejercicio.content || ejercicio.variations || []) as any,
        };

        if (!exists) {
          newBloquesToAdd.push(nuevoEjercicio);
        }
        newSecItems.push({ kind: 'BLOQUE' as const, code: ejercicio.code! });

        // Validacin pedaggica de BPMs
        if (alumno?.nivelTecnico && ejercicio.targetPPMs?.length) {
          const target = ejercicio.targetPPMs.find(t => t.nivel === alumno.nivelTecnico);
          if (!target) {
            toast.warning(
              ` Este ejercicio no tiene objetivos definidos para el Nivel Tcnico ${alumno.nivelTecnico}`,
              { duration: 5000 }
            );
          }
        }
      }
    });

    setFormData(prev => ({
      ...prev,
      bloques: [...prev.bloques, ...newBloquesToAdd],
      secuencia: [...prev.secuencia, ...newSecItems],
    }));
    setSelectedEjercicios(new Set());
  };

  const crearRondaDesdeSeleccion = () => {
    const codigosSeleccionados: string[] = [];
    const newBloquesToAdd: Ejercicio[] = [];

    selectedEjercicios.forEach(ejercicioId => {
      const ejercicio = ejercicios.find(e => e.id === ejercicioId);
      if (ejercicio) {
        const existsInSession = formData.bloques.some(b => b.code === ejercicio.code) || newBloquesToAdd.some(b => b.code === ejercicio.code);
        if (!existsInSession) {
          const nuevoEjercicio = {
            nombre: ejercicio.nombre,
            code: ejercicio.code,
            tipo: ejercicio.tipo,
            duracionSeg: ejercicio.duracionSeg,
            instrucciones: ejercicio.instrucciones,
            indicadorLogro: ejercicio.indicadorLogro,
            materialesRequeridos: ejercicio.materialesRequeridos || [],
            media: ejercicio.media || {},
            elementosOrdenados: ejercicio.elementosOrdenados || [],
            // Include variations in snapshot
            // Include variations in snapshot
            variations: (ejercicio.variations || ejercicio.content || []) as any as Variation[],
            content: (ejercicio.content || ejercicio.variations || []) as any,
          };
          newBloquesToAdd.push(nuevoEjercicio);
        }
        if (ejercicio.code) {
          codigosSeleccionados.push(ejercicio.code);
        }
      }
    });

    if (codigosSeleccionados.length === 0) return;

    const nuevaRonda = {
      id: uid(),
      bloques: codigosSeleccionados,
      repeticiones: 1,
      aleatoria: false
    };

    let nuevoSeqIndex: number;
    setFormData(prev => {
      const nuevaSecuencia: SecuenciaItem[] = [...prev.secuencia, { kind: 'RONDA', id: nuevaRonda.id }];
      nuevoSeqIndex = nuevaSecuencia.length - 1; // ndice de la nueva ronda en la secuencia

      return {
        ...prev,
        bloques: [...prev.bloques, ...newBloquesToAdd],
        rondas: [...prev.rondas, nuevaRonda],
        secuencia: nuevaSecuencia,
      };
    });
    setSelectedEjercicios(new Set());
    // Expandir la nueva ronda automticamente (usando el ndice en la secuencia)
    setExpandedRondas(prev => new Set([...prev, nuevoSeqIndex]));
  };

  /* const updateEjercicioInline = (index: number, updatedEjercicio: Ejercicio) => {
    const newBloques = [...formData.bloques];
    newBloques[index] = updatedEjercicio;
    setFormData({ ...formData, bloques: newBloques });
    setEditingEjercicio(null);
    toast.success(' Ejercicio actualizado');
  }; */

  const removeEjercicio = (index: number) => {
    const ejercicio = formData.bloques[index];

    // Check if this exercise is used in any ronda
    const usedInRonda = formData.rondas.some(r => r.bloques.includes(ejercicio.code!));

    if (usedInRonda) {
      // Only remove from secuencia, not from bloques (keep it for rondas)
      const newSecuencia = formData.secuencia.filter(item =>
        !(item.kind === 'BLOQUE' && item.code === ejercicio.code)
      );
      setFormData({ ...formData, secuencia: newSecuencia });
      toast.success(' Ejercicio eliminado de la secuencia (sigue disponible en rondas)');
    } else {
      // Not used in any ronda, safe to remove completely
      const newBloques = formData.bloques.filter((_, i) => i !== index);
      const newSecuencia = formData.secuencia.filter(item =>
        !(item.kind === 'BLOQUE' && item.code === ejercicio.code)
      );
      setFormData({ ...formData, bloques: newBloques, secuencia: newSecuencia });
      toast.success(' Ejercicio eliminado completamente');
    }
  };

  const removeEjercicioFromRonda = (rondaIndex: number, ejercicioCode: string) => {
    const newRondas = [...formData.rondas];
    newRondas[rondaIndex].bloques = newRondas[rondaIndex].bloques.filter(c => c !== ejercicioCode);
    if (newRondas[rondaIndex].bloques.length === 0) {
      newRondas.splice(rondaIndex, 1);
    }
    setFormData(prev => ({ ...prev, rondas: newRondas }));
  };

  const updateRondaRepeticiones = (rondaId: string, reps: string) => {
    const newRondas = formData.rondas.map(r =>
      r.id === rondaId ? { ...r, repeticiones: Math.max(1, parseInt(reps) || 1) } : r
    );
    setFormData({ ...formData, rondas: newRondas });
  };

  const updateRondaAleatoria = (rondaId: string, aleatoria: boolean) => {
    const newRondas = formData.rondas.map(r =>
      r.id === rondaId ? { ...r, aleatoria: !!aleatoria } : r
    );
    setFormData({ ...formData, rondas: newRondas });
  };

  // Toggle between 'foco' and 'repaso' mode for an exercise
  const toggleEjercicioModo = (index: number) => {
    const newBloques = [...formData.bloques];
    const currentModo = newBloques[index].modo || 'foco';
    newBloques[index] = { ...newBloques[index], modo: currentModo === 'foco' ? 'repaso' : 'foco' };
    setFormData(prev => ({ ...prev, bloques: newBloques }));
  };

  const duplicateRonda = (rondaId: string) => {
    const ronda = formData.rondas.find(r => r.id === rondaId);
    if (!ronda) return;

    const newRonda: Ronda = { ...JSON.parse(JSON.stringify(ronda)), id: uid() };
    const rondaIdx = formData.secuencia.findIndex(s => s.kind === 'RONDA' && s.id === rondaId);

    const newSecuencia = [...formData.secuencia];
    const nuevoSeqIndex = rondaIdx + 1; // ndice de la nueva ronda en la secuencia
    newSecuencia.splice(nuevoSeqIndex, 0, { kind: 'RONDA', id: newRonda.id });

    setFormData(prev => ({
      ...prev,
      rondas: [...prev.rondas, newRonda],
      secuencia: newSecuencia
    }));

    // Expandir la ronda duplicada automticamente
    setExpandedRondas(prev => new Set(Array.from(prev).concat(nuevoSeqIndex)));
    toast.success(' Ronda duplicada');
  };

  const handleDragEnd = (event: DragEndEvent) => {
    const { active, over } = event;
    if (!over || active.id === over.id) return;

    const activeId = String(active.id);
    const overId = String(over.id);

    // 1) Reordenar dentro de SECUENCIA (ambos son seq-${index})
    if (activeId.startsWith('seq-') && overId.startsWith('seq-') &&
      !activeId.includes('-r-') && !overId.includes('-r-')) {
      const oldIndex = parseInt(activeId.split('-').pop()!);
      const newIndex = parseInt(overId.split('-').pop()!);
      setFormData({
        ...formData,
        secuencia: arrayMove(formData.secuencia, oldIndex, newIndex),
      });
      return;
    }

    // 2) Mover dentro de una misma ronda
    if (activeId.includes('-r-') && overId.includes('-r-') &&
      activeId.split('-r-')[0] === overId.split('-r-')[0]) {
      const rondaId = activeId.split('-r-')[0];
      const ronda = formData.rondas.find(r => r.id === rondaId);
      if (!ronda) return;

      const oldIndex = parseInt(activeId.split('-').pop()!);
      const newIndex = parseInt(overId.split('-').pop()!);
      const newBloques = arrayMove(ronda.bloques, oldIndex, newIndex);

      setFormData({
        ...formData,
        rondas: formData.rondas.map(r =>
          r.id === rondaId ? { ...r, bloques: newBloques } : r
        ),
      });
      return;
    }

    // 3) Mover ejercicio de una ronda a otra ronda
    if (activeId.includes('-r-') && overId.includes('-r-') &&
      activeId.split('-r-')[0] !== overId.split('-r-')[0]) {
      const fromRondaId = activeId.split('-r-')[0];
      const toRondaId = overId.split('-r-')[0];
      const bloqueCode = activeId.split('-r-')[1].split('-')[0];
      const newIndex = parseInt(overId.split('-').pop()!);

      const newRondas = formData.rondas.map(r => {
        if (r.id === fromRondaId) {
          return { ...r, bloques: r.bloques.filter(code => code !== bloqueCode) };
        }
        if (r.id === toRondaId) {
          return {
            ...r,
            bloques: [
              ...r.bloques.slice(0, newIndex),
              bloqueCode,
              ...r.bloques.slice(newIndex),
            ],
          };
        }
        return r;
      });

      setFormData({ ...formData, rondas: newRondas });
      return;
    }

    // 4) Mover de SECUENCIA (bloque) a RONDA
    if (activeId.startsWith('seq-') && !activeId.includes('-r-') && overId.includes('-r-')) {
      const activeIndex = parseInt(activeId.split('-').pop()!);
      const activeItem = formData.secuencia[activeIndex];

      // Slo permitimos mover BLOQUE a ronda
      if (activeItem.kind !== 'BLOQUE') return;

      const bloqueCode = activeItem.code!;
      const rondaId = overId.split('-r-')[0];
      const newIndex = parseInt(overId.split('-').pop()!);

      // Remover de secuencia
      const newSecuencia = formData.secuencia.filter((_, idx) => idx !== activeIndex);

      // Aadir a ronda
      const newRondas = formData.rondas.map(r =>
        r.id === rondaId
          ? {
            ...r,
            bloques: [
              ...r.bloques.slice(0, newIndex),
              bloqueCode,
              ...r.bloques.slice(newIndex),
            ],
          }
          : r
      );

      setFormData({ ...formData, secuencia: newSecuencia, rondas: newRondas });
      return;
    }

    // 5) Mover de RONDA a SECUENCIA
    if (activeId.includes('-r-') && overId.startsWith('seq-') && !overId.includes('-r-')) {
      const rondaId = activeId.split('-r-')[0];
      const bloqueCode = activeId.split('-r-')[1].split('-')[0];
      const newIndex = parseInt(overId.split('-').pop()!);

      // Remover de ronda
      const newRondas = formData.rondas.map(r =>
        r.id === rondaId
          ? { ...r, bloques: r.bloques.filter(code => code !== bloqueCode) }
          : r
      );

      // Aadir a secuencia
      const newSecuencia = [...formData.secuencia];
      newSecuencia.splice(newIndex, 0, { kind: 'BLOQUE', code: bloqueCode });

      setFormData({ ...formData, secuencia: newSecuencia, rondas: newRondas });
      return;
    }
  };

