                                    const isActive = realIdx === indiceActual;
                                    const isCompleted = completados.has(realIdx);
                                    const isOmitted = omitidos.has(realIdx);

                                    // Format label for breadcrumb
                                    let label = ej.tipo;
                                    if (ej.esRonda && ej.rondaIdx !== undefined && ej.repeticion !== undefined) {
