      if (profesorId) {
        // Verificar que sea un UUID vlido antes de incluir
        const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
        if (uuidRegex.test(String(profesorId).trim())) {
          dataToSave.profesorAsignadoId = profesorId;
        } else {
          // Si no es UUID vlido, rechazar el cambio
          setSaveResult({ success: false, message: ' El ID del profesor asignado no es vlido. Debe ser un UUID vlido.' });
          toast.error('El ID del profesor asignado no es vlido. Debe ser un UUID vlido.');
