  // Cambiar contrasea usando resetPasswordForEmail (enva email para restablecer contrasea)
  const changePasswordMutation = useMutation({
    mutationFn: async () => {
      // Determinar el email del usuario objetivo
      let targetEmail;

      if (isEditingOwnProfile) {
        // Usuario editando su propio perfil: usar email de la sesin activa
        const { data: { session } } = await supabase.auth.getSession();
        if (!session?.user?.email) {
          throw new Error('No hay sesin activa o no se pudo obtener el email. Por favor, inicia sesin de nuevo.');
        }
        targetEmail = session.user.email;
        // Usar el helper compartido (funciona para el usuario autenticado)
        await sendPasswordResetEmailFor(targetEmail);
      } else {
        // Admin editando otro usuario: usar Edge Function con Admin API
        if (!targetUser?.email) {
          throw new Error('No se pudo obtener el email del usuario.');
        }
        if (!targetUser?.id) {
          throw new Error('No se pudo obtener el ID del usuario.');
        }
        targetEmail = targetUser.email;
        // Usar Edge Function que usa Admin API (ms confiable para otros usuarios)
        await sendPasswordResetEmailFor(targetEmail);
      }
      return { success: true, email: targetEmail };
    },
    onSuccess: (data) => {
      const email = data?.email || targetUser?.email || effectiveUser?.email || 'tu correo';
      const mensaje = isEditingOwnProfile
        ? ` Te hemos enviado un correo a ${email} para cambiar tu contrasea.`
        : ` Se ha enviado un correo a ${email} para cambiar la contrasea.`;
      setPasswordResult({
        success: true,
        message: mensaje
      });
      setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' });
      toast.success(isEditingOwnProfile
        ? 'Te hemos enviado un correo para cambiar tu contrasea.'
        : 'Se ha enviado un correo para cambiar la contrasea.');
    },
    onError: (error) => {
      log.error('[PerfilModal] Error al enviar email de restablecimiento:', error);
      const errorMessage = error.message || 'No se pudo enviar el correo';
      setPasswordResult({
        success: false,
        message: ` Error: ${errorMessage} `
      });
      toast.error(`Error al enviar el correo: ${errorMessage} `);
