  // Cambiar contrasea directamente (solo para el propio usuario)
  const updatePasswordMutation = useMutation<any, Error, { currentPassword: string, newPassword: string }>({
    mutationFn: async ({ currentPassword, newPassword }) => {
      if (!isEditingOwnProfile) {
        throw new Error('Solo puedes cambiar tu propia contrasea directamente.');
      }

      // Obtener el email del usuario actual
      const { data: { session } } = await supabase.auth.getSession();
      if (!session?.user?.email) {
        throw new Error('No se pudo obtener la informacin de la sesin.');
      }

      // Verificar la contrasea actual intentando hacer signIn
      const { error: signInError } = await supabase.auth.signInWithPassword({
        email: session.user.email,
        password: currentPassword.trim(),
      });

      if (signInError) {
        if (signInError.message?.includes('Invalid login credentials') ||
          signInError.message?.includes('Email not confirmed')) {
          throw new Error('La contrasea actual es incorrecta.');
        }
        throw signInError;
      }

      // Si la contrasea actual es correcta, actualizar a la nueva
      const { error } = await supabase.auth.updateUser({
        password: newPassword.trim(),
      });

      if (error) {
        throw error;
      }

      return { success: true };
    },
    onSuccess: () => {
      setPasswordResult({
        success: true,
        message: ' Contrasea actualizada correctamente.'
      });
      setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' });
      toast.success('Contrasea actualizada correctamente');
    },
    onError: (error) => {
      log.error('[PerfilModal] Error al actualizar contrasea:', error);
      const errorMessage = error.message || 'No se pudo actualizar la contrasea';
      setPasswordResult({
        success: false,
        message: ` Error: ${errorMessage} `
