        success: false,
        message: ' Por favor, introduce tu contrasea actual.'
      });
      return;
    }

    if (!passwordData.newPassword.trim()) {
      setPasswordResult({
        success: false,
        message: ' Por favor, introduce una nueva contrasea.'
      });
      return;
    }

    if (passwordData.newPassword.length < 6) {
      setPasswordResult({
        success: false,
        message: ' La contrasea debe tener al menos 6 caracteres.'
      });
      return;
    }

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setPasswordResult({
        success: false,
        message: ' Las contraseas no coinciden.'
      });
      return;
    }

    updatePasswordMutation.mutate({
      currentPassword: passwordData.currentPassword,
