    FeedbackSemanal,
    HabilidadesMaestras,
    StudiaUser,
    LevelConfig,
    UserRole,
    MediaItem,
    CriteriaStatus
} from "@/features/shared/types/domain";
import {
    Drawer,
    DrawerContent,
    DrawerHeader,
    DrawerTitle,
    DrawerDescription,
} from "@/features/shared/components/ui/drawer";
import { Button } from "@/features/shared/components/ds/Button";
import { Label } from "@/features/shared/components/ui/label";
import { Textarea } from "@/features/shared/components/ui/textarea";
